    function freadbyte($f) {
        return ord(fread($f,1));
    };
    function freadword($f) {
        $b1=freadbyte($f);
        $b2=freadbyte($f);
        return $b2*256+$b1;
    };

    function freadlngint($f) {
        return freaddword($f);
    };
    function freaddword($f) {
        $b1=freadword($f);
        $b2=freadword($f);
        return $b2*65536+$b1;
    };

    function retbits($byte,$start,$len) {
        $bin=decbin8($byte);
