        $hred=dechex($red);
        if(strlen($hred)==1) $hred="0$hred";
        $hgreen=dechex($green);
        if(strlen($hgreen)==1) $hgreen="0$hgreen";
        $hblue=dechex($blue);
        if(strlen($hblue)==1) $hblue="0$hblue";
        return($hred.$hgreen.$hblue);
    };
