                    $ret.=chr(0).chr(5).substr($retd,$t,5).chr(0);
                    $t+=1;
                }
                if(($t+6)%$width==0) {
                    $ret.=chr(0).chr(6).substr($retd,$t,6);
                    $t+=2;
                }
                else {
                    $ret.=chr(0).chr(4).substr($retd,$t,4);
                };
            };
            $ret.=chr(0).chr(1);
        }
        else {
            $ret.=$retd;
        };

