            if($bibitcount<24) {
                $img=imagecreate($width,$height);
                $colors=pow(2,$bibitcount);
                for($p=0;$p<$colors;$p++) {
                    $b=freadbyte($f);
                    $g=freadbyte($f);
                    $r=freadbyte($f);
