                        $prefix=freadbyte($f);
                        $suffix=freadbyte($f);
                        $pocetb+=2;
                        if($echoit) echo "prefix: $prefix suffix: $suffix<br>";
                    };
                    for($x=0;$x<strlen($data);$x++) {
                        imagesetpixel($img,$x,$y,$palette[ord($data[$x])]);
