                            $pocet=$suffix;
                            $currentbit=0;
                            for($h=0;$h<$pocet;$h++)
                                $data.=chr(freadbits($f,4));
                            if($currentbit!=0) freadbits($f,4);
                            $pocetb+=ceil(($pocet/2));
                            if($pocetb%2==1) {
                                freadbyte($f);
                                $pocetb++;
                            };
                        };
                        if($prefix>0) {
                            $pocet=$prefix;
                            $i=0;
                            for($r=0;$r<$pocet;$r++) {
                                if($i%2==0) {
                                    $data.=chr($suffix%16);
                                }
                                else {
                                    $data.=chr(floor($suffix/16));
                                };
                                $i++;
                            };
                        };
